-- ============================================================================

-- ============================================================================
-- HELPER FUNCTION: Generate invoice number
-- ============================================================================
CREATE OR REPLACE FUNCTION fixture_invoice_number(p_year INT, p_month INT, p_seq INT)
RETURNS TEXT AS $$
BEGIN
  RETURN 'INV-' || p_year || '-' || LPAD(p_month::TEXT, 2, '0') || '-' || LPAD(p_seq::TEXT, 4, '0');
END;
$$ LANGUAGE plpgsql;

