-- Drop helper function
DROP FUNCTION IF EXISTS fixture_invoice_number(INT, INT, INT);

-- ============================================================================
-- VERIFICATION
-- ============================================================================
DO $$
DECLARE
  v_total INT;
  v_paid INT;
  v_partial INT;
  v_pending INT;
  v_overdue INT;
  v_void INT;
  v_total_amount NUMERIC;
  v_paid_amount NUMERIC;
BEGIN
  SELECT COUNT(*) INTO v_total FROM invoices WHERE id::text LIKE 'ee000001-%';
  SELECT COUNT(*) INTO v_paid FROM invoices WHERE status = 'paid' AND id::text LIKE 'ee000001-%';
  SELECT COUNT(*) INTO v_partial FROM invoices WHERE status = 'partially_paid' AND id::text LIKE 'ee000001-%';
  SELECT COUNT(*) INTO v_pending FROM invoices WHERE status = 'unpaid' AND id::text LIKE 'ee000001-%';
  SELECT COUNT(*) INTO v_overdue FROM invoices WHERE status = 'unpaid' AND id::text LIKE 'ee000001-%';
  SELECT COUNT(*) INTO v_void FROM invoices WHERE status = 'void' AND id::text LIKE 'ee000001-%';
  SELECT COALESCE(SUM(amount_due), 0) INTO v_total_amount FROM invoices WHERE id::text LIKE 'ee000001-%';
  SELECT COALESCE(SUM(amount_paid), 0) INTO v_paid_amount FROM invoices WHERE id::text LIKE 'ee000001-%';

  RAISE NOTICE 'Invoices Created:';
  RAISE NOTICE '  Total Invoices: %', v_total;
  RAISE NOTICE '  Paid: %', v_paid;
  RAISE NOTICE '  Partially Paid: %', v_partial;
  RAISE NOTICE '  Pending: %', v_pending;
  RAISE NOTICE '  Overdue: %', v_overdue;
  RAISE NOTICE '  Void: %', v_void;
  RAISE NOTICE '  Total Amount Due: %', v_total_amount;
  RAISE NOTICE '  Total Amount Paid: %', v_paid_amount;
END $$;
