-- ============================================================================
-- VERIFICATION
-- ============================================================================
DO $$
DECLARE
  v_total INT;
  v_with_checkout INT;
  v_still_inside INT;
  v_flagged INT;
  v_august INT;
  v_december INT;
  v_january INT;
BEGIN
  SELECT COUNT(*) INTO v_total FROM access_logs WHERE id::text LIKE 'ad000001-%';
  SELECT COUNT(*) INTO v_with_checkout FROM access_logs WHERE check_out_time IS NOT NULL AND id::text LIKE 'ad000001-%';
  SELECT COUNT(*) INTO v_still_inside FROM access_logs WHERE check_out_time IS NULL AND id::text LIKE 'ad000001-%';
  SELECT COUNT(*) INTO v_flagged FROM access_logs WHERE flagged = true AND id::text LIKE 'ad000001-%';
  SELECT COUNT(*) INTO v_august FROM access_logs WHERE check_in_time >= '2025-08-01' AND check_in_time < '2025-09-01' AND id::text LIKE 'ad000001-%';
  SELECT COUNT(*) INTO v_december FROM access_logs WHERE check_in_time >= '2025-12-01' AND check_in_time < '2026-01-01' AND id::text LIKE 'ad000001-%';
  SELECT COUNT(*) INTO v_january FROM access_logs WHERE check_in_time >= '2026-01-01' AND id::text LIKE 'ad000001-%';

  RAISE NOTICE 'Access Logs Created:';
  RAISE NOTICE '  Total Entries: %', v_total;
  RAISE NOTICE '  With Checkout: %', v_with_checkout;
  RAISE NOTICE '  Still Inside: %', v_still_inside;
  RAISE NOTICE '  Flagged: %', v_flagged;
  RAISE NOTICE '  August 2025: %', v_august;
  RAISE NOTICE '  December 2025: %', v_december;
  RAISE NOTICE '  January 2026: %', v_january;
END $$;
