-- ============================================================================
-- VERIFICATION
-- ============================================================================
DO $$
DECLARE
  v_total INT;
  v_create INT;
  v_update INT;
  v_delete INT;
  v_verify INT;
  v_approve INT;
  v_assign INT;
BEGIN
  SELECT COUNT(*) INTO v_total FROM audit_logs WHERE id::text LIKE 'bf000001%';
  SELECT COUNT(*) INTO v_create FROM audit_logs WHERE id::text LIKE 'bf000001%' AND action = 'CREATE';
  SELECT COUNT(*) INTO v_update FROM audit_logs WHERE id::text LIKE 'bf000001%' AND action = 'UPDATE';
  SELECT COUNT(*) INTO v_delete FROM audit_logs WHERE id::text LIKE 'bf000001%' AND action = 'DELETE';
  SELECT COUNT(*) INTO v_verify FROM audit_logs WHERE id::text LIKE 'bf000001%' AND action = 'VERIFY';
  SELECT COUNT(*) INTO v_approve FROM audit_logs WHERE id::text LIKE 'bf000001%' AND action = 'APPROVE';
  SELECT COUNT(*) INTO v_assign FROM audit_logs WHERE id::text LIKE 'bf000001%' AND action IN ('ASSIGN', 'UNASSIGN');

  RAISE NOTICE '';
  RAISE NOTICE '=== Audit Logs Fixture Verification ===';
  RAISE NOTICE 'Total Audit Logs: % (target: 250)', v_total;
  RAISE NOTICE '  By Action:';
  RAISE NOTICE '    - CREATE: % (target: ~100)', v_create;
  RAISE NOTICE '    - UPDATE: % (target: ~100)', v_update;
  RAISE NOTICE '    - DELETE: % (target: ~30)', v_delete;
  RAISE NOTICE '    - VERIFY: %', v_verify;
  RAISE NOTICE '    - APPROVE: %', v_approve;
  RAISE NOTICE '    - ASSIGN/UNASSIGN: %', v_assign;
  RAISE NOTICE '=======================================';
END $$;
